import React, {useState} from "react";
import styled from "styled-components";

const Prediction = styled.div`
  font-size: 20px;
  color: white;
  margin-bottom: 32px;
`;

const EmptyImage = styled.div`
  background-color: lightGrey;
  height: 320px; 
  width: 320px;
  margin: 0 auto;
`;

const Radio = styled.div`

`;

const SubmitButton = styled.button`

`;

const Overlay = styled.div`
  position: fixed;
  width: 100%;
  left: 0;
  top: 0;
  bottom: 0;
  right: 0;
  background: #50000000;
`;

const Modal = styled.div`
  width: fit-content;
  height: fit-content;
  position: fixed;
  margin: 0 auto;
  left: 50%;
  transform: translate(-50%);
  top: 50%;
`;

const PredictionModal = ({show, prediction, image, setPrediction, setShowPrediction, setAccuracyRating}) => {
  const [guessedCorrect, setGuessedCorrect] = useState(1);


  const saveAccuracy = () => {
    fetch("api/add-to-accuracy",
      {
        method: "post",
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({accurate: guessedCorrect})
      })
      .then(res => {
        res.json().then(rating => {
          setAccuracyRating(rating.percentage);
          setShowPrediction(false);
          setPrediction();
        });
      })
  }

  return show ?
    <Overlay>
      <Modal>
        <div>
          {
            prediction ?
              <Prediction>
                <div>
                  {prediction.className[0].toUpperCase() + prediction.className.slice(1)}
                </div>
                <div>
                  {`(${(prediction.probability * 100).toFixed(2)}% sure)`}
                </div>
              </Prediction> :
              <div style={{display: "inline-block", height: "52px", marginBottom: "32px"}}/>
          }
        </div>
        <div>
          {image ?
            <img alt="The item to be predicted" src={image} height={300}/> :
            <EmptyImage/>
          }
        </div>
        <div>
          {
            prediction && show &&
            <>
              <h3>Was my guess correct?</h3>
              <Radio onChange={(e) => setGuessedCorrect(e.target.value)}>
                <input type="radio" value={1} name="correct"/> Yes
                <input type="radio" value={0} name="correct"/> No
              </Radio>
              <SubmitButton onClick={saveAccuracy}>save</SubmitButton>
            </>
          }
        </div>
      </Modal>
    </Overlay> :
    <></>;
}


export default PredictionModal;
